APlayerController* ABaseWeapon::GetPlayerController() const {
	const auto Player = Cast<ACharacter>(GetOwner());
	if (!Player) return nullptr;
	AActor* ComponentOwner = GetOwner();
	
	return Player->GetController<APlayerController>();
	
}
